// CommentFormPage.js
import React, { useState } from 'react';
import { submitComment } from './front-back-end-link';
import './CommentFormPage.css';

const CommentFormPage = ({ tracks }) => {
const [comment, setComment] = useState('');
const [selectedTrackIndex, setSelectedTrackIndex] = useState(0);

const handleSubmit = (event) => {
    event.preventDefault();
    submitComment(selectedTrackIndex, comment)
    .then(data => console.log(data))
    .catch(error => console.error(error));
    // todo: we would likely want to do something after the comment is submitted,
    // like showing a success message, clearing the form, or redirecting again.. 
};

return (
    <div className = "comment-form-page">
<form onSubmit={handleSubmit}>

<label>

Comment:

<input type="text" value={comment} onChange={e => setComment(e.target.value)} />

</label>

<label>

Track:

<select value={selectedTrackIndex} onChange={e => setSelectedTrackIndex(e.target.value)}>

{tracks.map((track, index) => (

<option key={index} value={index}>{track.name}</option>

))}

</select>

</label>

<button type="submit">Submit Comment</button>

</form>
    </div>
);
};

export default CommentFormPage;
