// CommentFormPage.js
import React, { useState, useEffect } from 'react';
import { submitComment } from './front-back-end-link';
import './CommentFormPage.css';

const CommentFormPage = () => {
  const [comment, setComment] = useState('');
  const [selectedTrackIndex, setSelectedTrackIndex] = useState('');
  const [tracks, setTracks] = useState([]);

  // Fetch the tracks from the backend when the component mounts
  useEffect(() => {
    fetch('http://localhost:8000/home/api/tracks_info')
      .then(response => {
        if (!response.ok) {
          throw new Error('Network response was not ok');
        }
        return response.json();
      })
      .then(data => {
        setTracks(data);
        // Set the default selected track index
        if (data.length > 0) {
          setSelectedTrackIndex(data[0].id);
        }
      })
      .catch(error => {
        console.error('Failed to fetch tracks:', error);
      });
  }, []);

  // Handle the form submission
  const handleSubmit = (event) => {
    event.preventDefault();
    const trackId = tracks[selectedTrackIndex] ? tracks[selectedTrackIndex].id : null;
    if (trackId) {
      submitComment(trackId, comment)
        .then(data => {
          console.log(data);
          setComment(''); // Clear comment field on success
          setSelectedTrackIndex(''); // Reset selected track index or set to default
          // Further actions on success like showing a success message
        })
        .catch(error => {
          console.error('Failed to submit comment:', error);
          // Further actions on failure like showing an error message
        });
    }
  };

return (
    <div className="comment-form-page">
    <h2>Leave a Comment</h2>
    <form onSubmit={handleSubmit} className="comment-form">
        <div className="form-group">
        <label htmlFor="comment">
            Comment:
            <input
            type="text"
            value={comment}
            onChange={e => setComment(e.target.value)}
            required
            />
        </label>
        </div>
        <div className="form-group">
        <label htmlFor="track-select">
            Track:
            <select
            value={selectedTrackIndex}
            onChange={e => setSelectedTrackIndex(e.target.value)}
            required
            >
            {tracks.map((track, index) => (
                <option key={track.id} value={index}>
                {track.name}
                </option>
            ))}
            </select>
        </label>
        </div>
        <button type="submit" className="submit-button">Submit Comment</button>
    </form>
    </div>
  );
};

export default CommentFormPage;
