// CommentFormPage.js
import React, { useState, useEffect, useMemo } from 'react';
import { submitComment } from './front-back-end-link';
import './CommentFormPage.css';

const CommentFormPage = () => {
const [comment, setComment] = useState('');
const [selectedTrackIndex, setSelectedTrackIndex] = useState('');
const [tracks, setTracks] = useState([]);
const [isLoading, setIsLoading] = useState(false);


  // Fetch the tracks from the backend when the component mounts
  useEffect(() => {
    setIsLoading(true);
    Promise.all([
        fetch('http://localhost:8000/home/api/tracks_info'),
        fetch('http://localhost:8000/home/api/comments')
      ])
      .then(response => {
        if (!response.ok) {
          throw new Error('Network response was not ok');
        }
        return response.json();
      })
      .then(data => {
        console.log('Tracks fetched:', data); // Log the fetched data
        setTracks(data);
        if (data.length > 0) {
          setSelectedTrackIndex(data[0].id); // Assume the id is the correct value for selectedTrackIndex
        }
      })
      .catch(error => {
        console.error('Failed to fetch tracks:', error);
      })
      .finally(() => {
        setIsLoading(false); // This will be executed after the promise is either fulfilled or rejected
      });
  }, []);


  // Handle the form submission
  const handleSubmit = (event) => {
    event.preventDefault();
    setIsLoading(true); // Set loading before the API call
    submitComment(selectedTrackIndex, comment)
      .then(data => {
        console.log(data);
        setComment('');
        // Fetch comments again to refresh the list
        return fetch('http://localhost:8000/home/api/comments');
      })
      .then(response => response.json())
      .then(data => {
        setComment(data); // Update comments with the latest list
      })
      .catch(error => {
        console.error('Failed to submit comment:', error);
      })
      .finally(() => {
        setIsLoading(false);
      });
  };

return (
    <div className="comment-form-page">
      <h2>Leave a Comment</h2>
      <form onSubmit={handleSubmit} className="comment-form">
        <div className="form-group">
          <label htmlFor="comment">
            <textarea
            id="comment"
            value={comment}
            onChange={e => setComment(e.target.value)}
            placeholder="I love this song! &#128516;" // This is where you set the placeholder text
            required
            />
          </label>
        </div>
        <div className="form-group">
          <label htmlFor="track-select">
            <b>Song Name:</b>
            {isLoading ? (
              <div>Loading...</div> // Loading indicator
            ) : (
              <select
                id="track-select"
                value={selectedTrackIndex}
                onChange={e => setSelectedTrackIndex(e.target.value)}
                required
              >
                {tracks.map((track) => (
                  <option key={track.track_uri} value={track.track_uri}>
                    {track.track_name}
                  </option>
                ))}
              </select>
            )}
          </label>
        </div>
        <button type="submit" className="submit-button">Submit Comment</button>
      </form>
    </div>
  );

};

export default CommentFormPage;
