// CommentFormPage.js
import React, { useState } from 'react';
import { submitComment } from './front-back-end-link';
import './CommentFormPage.css';

const CommentFormPage = ({ tracks }) => { // Default to an empty array if tracks is not passed
  const [comment, setComment] = useState('');
  // Initialize selectedTrackIndex only if tracks are available
  const [selectedTrackIndex, setSelectedTrackIndex] = useState('');

    // When the component receives tracks for the first time, update the selected index
    useEffect(() => {
        if (tracks.length > 0) {
          setSelectedTrackIndex(tracks[0].id);
        }
      }, [tracks]);

  const handleSubmit = (event) => {
    event.preventDefault();
    // Ensure that a track is selected before submitting
    if (selectedTrackIndex) {
      submitComment(selectedTrackIndex, comment)
        .then(data => {
          console.log(data);
          setComment(''); // Clear the comment field on success
          // Handle further success actions (showing a success message, etc.)
        })
        .catch(error => {
          console.error(error);
          // Handle error (show error message, etc.)
        });
    } else {
      console.error('No track selected');
      // Handle the case where no track is selected (show error message, etc.)
    }
  };

  return (
    <div className="comment-form-page">
      <h2>Leave a Comment</h2>
      <form onSubmit={handleSubmit} className="comment-form">
        <div className="form-group">
          <label htmlFor="comment">Comment:</label>
          <textarea
            id="comment"
            value={comment}
            onChange={e => setComment(e.target.value)}
            placeholder="Your thoughts..."
            required // Make the textarea required for form submission
          />
        </div>

        <div className="form-group">
          <label htmlFor="track-select">Track:</label>
          <select
            id="track-select"
            value={selectedTrackIndex}
            onChange={e => setSelectedTrackIndex(e.target.value)}
            required // Make the select required for form submission
          >
            {tracks.map((track) => (
              <option key={track.id} value={track.id}>{track.name}</option>
            ))}
          </select>
        </div>

        <button type="submit" className="submit-button">Submit Comment</button>
      </form>
    </div>
  );
};

export default CommentFormPage;
