import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Route, Switch, Link } from 'react-router-dom';
import { useMenuLogic } from './user-menu';
import { submitComment } from './front-back-end-link';
import CommentFormPage from './CommentFormPage';
import './App.css';
import './animations.css';
import './user-menu.css';


function Card({ title, className, children }) {
    return (
      <div className={`card-container__card ${className}`}>
        <h2>{title}</h2>
        {children}
    </div>
    );
}

function ClickableLink({ id, className, children }) {
  return <div id={id} className={`clickable ${className}`}>{children}</div>;
}

function App() {
  const [comment, setComment] = useState('');
  const [selectedTrackIndex, setSelectedTrackIndex] = useState(0);
  const [tracks, setTracks] = useState([]);

  // attempt to fetch the tracks from the backend when the component mounts
  useEffect(() => {
    fetch('http://localhost:8000/api/tracks_info') 
      .then(response => {
        if (!response.ok) {
          throw new Error('Network response was not ok');
        }
        return response.json();
      })
      .then(data => {
        setTracks(data); // use this data to set the tracks in your component state
      })
      .catch(error => {
        console.error('Failed to fetch tracks:', error);
      });
  }, []);


  //todo: allow user profile menu to be closed by clicking anywhere on the screen
  const { isMenuOpen, toggleMenu, closeMenu, handleMenuClick } = useMenuLogic();

  const handleSubmit = (event) => {
    event.preventDefault();
    submitComment(selectedTrackIndex, comment)
      .then(data => {       // handle the response from the backend
        console.log(data);  // update the UI accordingly
      })
      .catch(error => {     // handle any errors that occur during the request
        console.error(error);
      });
  };

  // main app component
  return (

      <div className = "app">
        <header className = "toolbar">
          <div className = "toolbar__title">
            <h1>Lyrical Link</h1>
          </div>
          <section className = "toolbar__options">
            <button className = "toolbar__sign-up glow-button">Sign Up</button>
            <button className = "toolbar__sign-up glow-button">Log In</button>
            <button onClick={toggleMenu} className = "toolbar__user-profile menu-toggle"></button>
          </section>
        </header>
        <section className = "hero-section">
          <div className = "hero-section__content__wrapper">
          <h1 className = "hero-section__caption fade-in-slide-in">All the best music. </h1>
          <button className ="hero-section__button fade-in-slide-in glow-button"> Explore</button>
          </div>
        </section>
        <div className="card-container">
          <Card title="Top Artists" className = "fade-in fade-speed1">
            <ClickableLink id="link1" className="link song"> Taylor Swift </ClickableLink>
            <ClickableLink id="link1" className="link"> Drake</ClickableLink>
            <ClickableLink id="link1" className="link"> Bad Bunny</ClickableLink>
            <ClickableLink id="link1" className="link"> SZA </ClickableLink>
            <ClickableLink id="link1" className="link"> Tyler, The Creator</ClickableLink>
            <ClickableLink id="link1" className="link"> ... </ClickableLink>
          </Card>
          <Card title="Top Songs" className = "fade-in fade-speed2">
            <ClickableLink id="link2" className="link"> Cruel Summer </ClickableLink>
            <ClickableLink id="link2" className="link"> Paint The Town Red</ClickableLink>
            <ClickableLink id="link2" className="link"> Snooze </ClickableLink>
            <ClickableLink id="link2" className="link"> I Remember Everything</ClickableLink>
            <ClickableLink id="link2" className="link"> Fast Car </ClickableLink>
            <ClickableLink id="link2" className="link"> ... </ClickableLink>
          </Card>
          <Card title="Top Albums" className = "fade-in fade-speed3">
            <p>This is some content for Card 3.</p>
            <ClickableLink id="link3" className="link">Link Example</ClickableLink>
          </Card>
        </div>

        {/* Slide out menu when user clicks on user profile*/}
        <div className={`slide-out-menu ${isMenuOpen ? 'open' : ''}`}>
          <div className="menu-item__photo"></div>
          <div className="menu-item">Contact Details</div>
        </div>
      </div>
  );
}

export default App;
