// CommentFormPage.js
import React, { useState } from 'react';
import { submitComment } from './front-back-end-link';
import './CommentFormPage.css';

const CommentFormPage = ({ tracks }) => {
  const [comment, setComment] = useState('');
  const [selectedTrackIndex, setSelectedTrackIndex] = useState(tracks.length > 0 ? tracks[0].id : 0);

  const handleSubmit = (event) => {
    event.preventDefault();
    submitComment(selectedTrackIndex, comment)
      .then(data => {
        console.log(data);
        // Handle success (clear form, show message, etc.)
      })
      .catch(error => {
        console.error(error);
        // Handle error (show error message, etc.)
      });
  };

  return (
    <div className="comment-form-page">
      <h2>Leave a Comment</h2>
      <form onSubmit={handleSubmit} className="comment-form">
        <div className="form-group">
          <label htmlFor="comment">Comment:</label>
          <textarea
            id="comment"
            value={comment}
            onChange={e => setComment(e.target.value)}
            placeholder="Your thoughts..."
          />
        </div>

        <div className="form-group">
          <label htmlFor="track-select">Track:</label>
          <select
            id="track-select"
            value={selectedTrackIndex}
            onChange={e => setSelectedTrackIndex(e.target.value)}
          >
            {tracks.map((track, index) => (
              <option key={index} value={track.id}>{track.name}</option>
            ))}
          </select>
        </div>

        <button type="submit" className="submit-button">Submit Comment</button>
      </form>
    </div>
  );
};

export default CommentFormPage;
