import React, { useState } from 'react';
import { submitComment } from './front-back-end-link';

const CommentFormPage = ({ tracks }) => {
const [comment, setComment] = useState('');
const [selectedTrackIndex, setSelectedTrackIndex] = useState(0);

const handleSubmit = (event) => {
    event.preventDefault();
    submitComment(selectedTrackIndex, comment)
    .then(data => console.log(data))
    .catch(error => console.error(error));
    //  we would likely want to do something after the comment is submitted,
    // like showing a success message, clearing the form, or redirecting again.. 
};

return (
    <div>
        <h1>Comment Form</h1>
    <form onSubmit={handleSubmit}>
        {/* ... form fields ... */}
    </form>
    </div>
);
};

export default CommentFormPage;
