// CommentFormPage.js
import React, { useState, useEffect } from 'react';
import { submitComment } from './front-back-end-link';
import './CommentFormPage.css';

const CommentFormPage = () => {
  const [comment, setComment] = useState('');
  const [selectedTrackIndex, setSelectedTrackIndex] = useState(0);
  const [tracks, setTracks] = useState([]);

  // Fetch tracks from the backend when the component mounts
  useEffect(() => {
    fetch('http://localhost:8000/home/api/tracks_info')
      .then(response => {
        if (!response.ok) {
          throw new Error('Network response was not ok');
        }
        return response.json();
      })
      .then(data => {
        setTracks(data);
      })
      .catch(error => {
        console.error('Failed to fetch tracks:', error);
      });
  }, []);

  const handleSubmit = (event) => {
    event.preventDefault();
    submitComment(selectedTrackIndex, comment)
      .then(data => {
        console.log(data);
        setComment(''); // Clear comment field on success
        // Further actions on success like showing a success message
      })
      .catch(error => {
        console.error('Failed to submit comment:', error);
        // Further actions on failure like showing an error message
      });
  };

  return (
    <div className="comment-form-page">
      <h2>Leave a Comment</h2>
      <form onSubmit={handleSubmit} className="comment-form">
        <div className="form-group">
          <label htmlFor="comment">
            Comment:
            <input
              type="text"
              value={comment}
              onChange={e => setComment(e.target.value)}
              required
            />
          </label>
        </div>
        <div className="form-group">
          <label htmlFor="track-select">
            Track:
            <select
              value={selectedTrackIndex}
              onChange={e => setSelectedTrackIndex(e.target.value)}
              required
            >
              {tracks.map((track, index) => (
                <option key={index} value={index}>{track.name}</option>
              ))}
            </select>
          </label>
        </div>
        <button type="submit" className="submit-button">Submit Comment</button>
      </form>
    </div>
  );
};

export default CommentFormPage;
