// CommentFormPage.js
import React, { useState, useEffect } from 'react';
import { submitComment } from './front-back-end-link';
import './CommentFormPage.css';

const CommentFormPage = ({ tracks }) => {
  const [comment, setComment] = useState('');
  const [selectedTrackIndex, setSelectedTrackIndex] = useState('');

  // Assuming tracks is an array of objects with an `id` and `name` property
  useEffect(() => {
    if (tracks && tracks.length > 0) {
      setSelectedTrackIndex(tracks[0].id); // Set to the id of the first track
    }
  }, [tracks]); // This will run when the `tracks` prop changes

  const handleSubmit = (event) => {
    event.preventDefault();
    submitComment(selectedTrackIndex, comment)
      .then(data => {
        console.log(data);
        setComment(''); // Clear the comment field on success
        // Handle further success actions like showing a success message
      })
      .catch(error => {
        console.error(error);
        // Handle errors such as displaying an error message
      });
  };

  return (
    <div className="comment-form-page">
      <h2>Leave a Comment</h2>
      <form onSubmit={handleSubmit} className="comment-form">
        <div className="form-group">
          <label htmlFor="comment">
            Comment:
            <textarea
              id="comment"
              value={comment}
              onChange={e => setComment(e.target.value)}
              placeholder="Your thoughts..."
              required
            />
          </label>
        </div>
        <div className="form-group">
          <label htmlFor="track-select">
            Track:
            <select
              id="track-select"
              value={selectedTrackIndex}
              onChange={e => setSelectedTrackIndex(e.target.value)}
              required
            >
              {tracks.map((track) => (
                <option key={track.id} value={track.id}>
                  {track.name}
                </option>
              ))}
            </select>
          </label>
        </div>
        <button type="submit" className="submit-button">Submit Comment</button>
      </form>
    </div>
  );
};

export default CommentFormPage;
