import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Route, Routes, Switch, Link } from 'react-router-dom';
import { useLocation } from 'react-router-dom'; 
import { useMenuLogic } from './user-menu';
import { submitComment } from './front-back-end-link';
import CommentFormPage from './CommentFormPage';
import './App.css';
import './animations.css';
import './user-menu.css';


function Card({ title, className, children }) {
    return (
      <div className={`card-container__card ${className}`}>
        <h2>{title}</h2>
        {children}
    </div>
    );
}

function ClickableLink({ id, className, children }) {
  return <div id={id} className={`clickable ${className}`}>{children}</div>;
}

function App() {
  // Fetching tracks, handling comments, and menu logic
  const [tracks, setTracks] = useState([]);
  const { isMenuOpen, toggleMenu } = useMenuLogic();

  // Fetch tracks from the backend when the component mounts
  useEffect(() => {
    fetch('http://localhost:8000/api/tracks_info') 
      .then(response => {
        if (!response.ok) {
          throw new Error('Network response was not ok');
        }
        return response.json();
      })
      .then(data => {
        setTracks(data); // use this data to set the tracks in your component state
      })
      .catch(error => {
        console.error('Failed to fetch tracks:', error);
      });
  }, []);

  const location = useLocation();

  return (
    <Router>
      <div className="app">
        <Toolbar toggleMenu={toggleMenu} isMenuOpen={isMenuOpen} />
        {location.pathname === '/' && <HeroSection />}
        <CardContainer />
        <Routes>
          <Route path="/comment-form" element={<CommentFormPage />} />
        </Routes>
      </div>
    </Router>
  );
}

function Toolbar({ toggleMenu }) {
  return (
    <header className="toolbar">
      {/* Toolbar content */}
    </header>
  );
}

function HeroSection() {
  return (
    <section className="hero-section">
      {/* Hero section content */}
    </section>
  );
}

function CardContainer() {
  return (
    <div className="card-container">
      {/* Cards for top artists, songs, albums */}
    </div>
  );
}

export default App;
