// CommentFormPage.js
import React, { useState, useEffect, useMemo } from 'react';
import { submitComment } from './front-back-end-link';
import './CommentFormPage.css';

const CommentFormPage = () => {
const [comment, setComment] = useState('');
const [selectedTrackIndex, setSelectedTrackIndex] = useState('');
const [tracks, setTracks] = useState([]);
const [isLoading, setIsLoading] = useState(false);


  // Fetch the tracks from the backend when the component mounts
  useEffect(() => {
    setIsLoading(true);
    Promise.all([
        fetch('http://localhost:8000/home/api/tracks_info'),
        fetch('http://localhost:8000/home/api/comments')
      ])
      .then(response => {
        if (!response.ok) {
          throw new Error('Network response was not ok');
        }
        return response.json();
      })
      .then(data => {
        console.log('Tracks fetched:', data); // Log the fetched data
        setTracks(data);
        if (data.length > 0) {
          setSelectedTrackIndex(data[0].id); // Assume the id is the correct value for selectedTrackIndex
        }
      })
      .catch(error => {
        console.error('Failed to fetch tracks:', error);
      })
      .finally(() => {
        setIsLoading(false); // This will be executed after the promise is either fulfilled or rejected
      });
  }, []);


  // Handle the form submission
  const handleSubmit = (event) => {
    event.preventDefault();
    // Find the track object by index, then use its track_uri to submit
    const trackUri = tracks[selectedTrackIndex] ? tracks[selectedTrackIndex].track_uri : null;
    if (trackUri) {
      submitComment(trackUri, comment)
        .then(data => {
          console.log(data);
          setComment(''); // Clear comment field on success
          // Further actions on success like showing a success message
        })
        .catch(error => {
          console.error('Failed to submit comment:', error);
          // Further actions on failure like showing an error message
        });
    }
  };

  return (
    <div className="comment-form-page">
      <h2>Leave a Comment</h2>
      <form onSubmit={handleSubmit} className="comment-form">
        {/* ... other form fields ... */}
        <div className="form-group">
          <label htmlFor="track-select">
            Track:
            <select
              id="track-select"
              value={selectedTrackIndex}
              onChange={e => setSelectedTrackIndex(e.target.value)}
              required
            >
              {tracks.map((track, index) => (
                <option key={track.track_uri} value={index}> {/* Use track_uri for key */}
                  {track.track_name} {/* Use track_name to display */}
                </option>
              ))}
            </select>
          </label>
        </div>
        {/* ... other form fields ... */}
      </form>
    </div>
  );
};

export default CommentFormPage;
